import styles from './button.module.scss';
import { motion } from 'framer-motion';

export default function Button({ title, style, callback }:
  { title: string, style: string, callback: (() => any) })
{
  var styleButton;
  if (style == 'primary') {
    styleButton = styles.primary;
  } else if (style == 'secondary') {
    styleButton = styles.secondary;
  } else {
    styleButton = styles.primary;
    console.warn('Button style not specified correctly - default to \'primary\' style'); 
  }
  return (
    <>
      <motion.button
        onClick={callback}
        whileHover={{
          scale: 1.1,
          borderRadius: '20px',
          boxShadow: '5px 5px 1px 1px #000000'
        }}
        whileTap={{
          scale: 0.8
        }}
      >
        {title}
      </motion.button>
    </>
  );
}
