import styles from './toast.module.scss';
import { motion } from 'framer-motion';
import ReactDOM from 'react-dom';

export function Toast({msg, interval}: {msg: string, interval: number}) {
  return (
    <>

        <div className={styles.toast}>{msg}</div>
    </>
  )
}
/**
 * 
<motion.div initial="hidden" animate={{
        opacity: [0, 1, 0],
        y: [0, 30, 0],
      }}
      transition={{
        times: [0, 0.1, 0.9],
        duration: interval,
        ease: "easeInOut"
      }}>
 */

/**
 * showToast returns the Toast component
 * @param msg 
 * @param interval 
 */
export function showToast(msg: string, interval: number) {
  ReactDOM.render (
    <Toast msg={msg} interval={interval} />
  )
}
