import styles from './toast.module.scss';
import { motion } from 'framer-motion';
import ReactDOM from 'react-dom';

const ToastMsg = (props) => {
  return (
    <>    
        <div className={styles.toast}>{props.msg}</div>
    </>
  )
}

export const ToastContainer = (props) => {

}
/**
 * 
<motion.div initial="hidden" animate={{
        opacity: [0, 1, 0],
        y: [0, 30, 0],
      }}
      transition={{
        times: [0, 0.1, 0.9],
        duration: interval,
        ease: "easeInOut"
      }}>
 */

/**
 * showToast returns the Toast component
 * @param msg 
 * @param interval 
 */
export function showToast(msg: string, interval: number) {
  ReactDOM.render (
    <Toast msg={msg} interval={interval} />
  )
}
