import Head from 'next/head';
import Link from 'next/link';
import React from 'react';

import Navbar from './navbar';

import styles from './layout.module.scss';
import utilStyles from '../styles/utils.module.scss';
import themeStyles from '../styles/theme.module.scss'

const name = 'Blog Master';

export const siteTitle = 'This is a Blog!'

var isDark: boolean = false;

const Theme = React.createContext();

export const useTheme = () => React.useContext(Theme);

export function ThemeProvider({ theme, children }) {
  const [val, setTheme] = React.useState(theme);
  return <Theme.Provider value={[val, setTheme]}>{children}</Theme.Provider>;
}

const cn = arr => arr.filter(Boolean).join(' ');


export default function Layout({ children, home }: { children: React.ReactNode, home?: boolean }) {

  return (
    <div className={`${styles.container}`} id="light">
      {/* Head tag for meta stuff */}
      <Head>
        <link rel="icon" href="/favicon.ico" />
        <meta name="description" content="Learn how to build a personal website using Next.js"/>
        <meta property="og:image" content={`https://og-image.now.sh/${encodeURI(
            siteTitle
          )}.png?theme=light&md=0&fontSize=75px&images=https%3A%2F%2Fassets.vercel.com%2Fimage%2Fupload%2Ffront%2Fassets%2Fdesign%2Fnextjs-black-logo.svg`}
        />
        <meta name="og:title" content={siteTitle} />
        <meta name="twitter:card" content="summary_large_image" />
      </Head>

      {/* button to control dark and light theme */}
      <button onClick={toggleTheme}>Toggle Theme</button>

      {/* Inserts NavBar on Every Page */}
      <Navbar home={home}></Navbar>

      {/* Displays Header as Link Conditionally */}
      <header className={styles.header}>
        {home ? (
          <>
            <img
              src="/images/linkedin.jpg"
              className={`${styles.headerHomeImage} ${utilStyles.borderCircle}`}
              alt={name}
            />
            <h1 className={utilStyles.heading2XL}>{name}</h1>
          </>
        ) : (
          <>
            <Link href="/">
              <a>
                <img
                  src="/images/linkedin.jpg"
                  className={`${styles.headerImage} ${utilStyles.borderCircle}`}
                  alt={name}
                />  
              </a>
            </Link>
            <h2 className={utilStyles.headingLg}>
              <Link href="/">
                <a className={utilStyles.colorInherit}>{name}</a>
              </Link>
            </h2>
          </>
        )}
      </header>

      {/* Main Content of Page */}
      <main>{children}</main>

      {/* Conditional Back Link */} 
      {!home && (
        <div className={styles.backToHome}>
          <Link href="/">
            <a>← Back to home</a>
          </Link>
        </div>
      )}
    </div>
  );
}

function toggleTheme() {
  if (typeof window !== 'undefined') {
    if (isDark) {
      document.getElementById('dark').id = 'light';
      isDark = false;
    } else {
      document.getElementById('light').id = 'dark';
      isDark = true;
    }
  } else {
    console.log("document object not available");
  }
}
