import { BehaviorSubject } from 'rxjs';

import Button from './button';
import { getThemeController } from './theme-provider';

// Imma use behavior subject because the subscription takes the current value before subscription and processes it
// AND.. you can add data to it easily and the subscription will process all of them as well
// AND... you can multicast your data
export const isDark = new BehaviorSubject(false);

// Button component to toggle the theme
export default function ToggleTheme() {
  // get the theme state and function to change it
  const [theme, setTheme] = getThemeController();
  var newTheme: boolean;

  return (
    <Button style="primary" callback={() => {
      // get the current dark/light value
      isDark.subscribe(dark => {
        setTheme(dark ? newTheme = false : newTheme = true);
      });
      isDark.next(newTheme);
    }}>
      {newTheme ? 'Day Mode' : 'Night Mode'}
    </Button>
  );
}
