import styles from './toast.module.scss';
import ReactDOM from 'react-dom';

const Toast = (props) => {
  return (
    <>    
        <div className={styles.toast}>{props.msg}</div>
    </>
  )
}

export const ToastContainer = (props) => {
  return (
    <div id="toast_container" className={styles.toast_container}></div>
  )
}

/**
 * ToastController controls addition and deletion of toasts
 */
export const ToastController = {
  delete: () => {

  },
  show: (msg: string) => {
    ReactDOM.render(
      <Toast msg={msg}></Toast>, document.getElementById('toast-container')
    );
  }
}


