import styles from './toast.module.scss';
import ReactDOM from 'react-dom';
import { isNullOrUndefined } from 'util';

const Toast = (props) => {
  return (
    <>    
        <div className={styles.toast}>{props.msg}</div>
    </>
  )
}

export const ToastContainer = (props) => {
  return (
    <div id="toast_container" className={styles.toast_container}></div>
  )
}

/**
 * ToastController controls addition and deletion of toasts in Toast Controller
 */
export const ToastController = {
  timeout: null,
  currentToast: false,
  container: document.getElementById('toast-container'),

  delete: () => {
    if (ToastController.container) {
      if (currentToast) {
        ReactDOM.unmountComponentAtNode(ToastController.container);
      }

    } else {
      console.error("You have to add a ToastContainer component to your page first")
    }
  },

  show: (msg: string) => {
    // check validity of toast controller
    if (ToastController.container) {
      ReactDOM.render(
        <Toast msg={msg}></Toast>, ToastController.container
      );
    } else {
      console.error("You have to add a ToastContainer component to your page first");
    }
    // set deletion timeout
    ToastController.timeout = setTimeout(ToastController.delete, 5000);
  }

}


