import styles from './toast.module.scss';
import ReactDOM from 'react-dom';

const Toast = (props) => {
  return (
    <>    
        <div className={styles.toast}>{props.msg}</div>
    </>
  )
}

export const ToastContainer = (props) => {
  return (
    <div id="toast_container" className={styles.toast_container}></div>
  )
}


/**
 * showToast returns the Toast component
 * @param msg 
 * @param interval 
 */
export function showToast(msg: string, interval: number) {
  ReactDOM.render (
    <Toast msg={msg} interval={interval} />
  )
}
