import styles from './toast.module.scss';
import { motion } from 'framer-motion';

export function Toast({msg: string, duration: number}) {
  return (
    <motion.div initial="hidden" animate={{
      opacity: [0, 1, 0],
      y: [0, 30, 0],
    }}
    transition={{
      times: [0, 0.1, 0.9],
      duration: 5,
      ease: "easeInOut"
    }}>
      <div className={styles.toast}>{msg}</div>
    </motion.div>
  )
}
