import fs from 'fs';
import path from 'path';
import matter from 'gray-matter';
import remark from 'remark';
import html from 'remark-html';

// get current posts folder under current working directory
const postsDirectory = path.join(process.cwd(), 'posts');

// get blog post data from markdown files
export function getSortedPostsData() {
  // get file names under /posts
  const fileNames = fs.readdirSync(postsDirectory);
  const allPostsData = fileNames.map((fileName) => {
    // remove '.md' from filename to get id
    const id = fileName.replace(/\.md$/, '');

    // read markdwon file as a string
    const fullPath = path.join(postsDirectory, fileName);
    const fileContents = fs.readFileSync(fullPath, 'utf8');

    // use gray-matter to parse the post metadata section
    const matterResult = matter(fileContents);

    return {
      id, 
      ...matterResult.data as {date: string, title: string}
    }
  });
  // sort posts by date
  return allPostsData.sort((a, b) => {
    if(a.date < b.date) {
      return 1;
    } else {
      return -1;
    }
  });
}

export function getAllPostIds() {
  // get all file names under /posts
  const fileNames = fs.readdirSync(postsDirectory)
  /**
    Returns array that looks like this:
    [
      {
        params: {
          id: 'ssg-ssr'
        }
      },
      {
        params: {
          id: 'pre-rendering'
        }
      }
    ]
   */
  return fileNames.map((fileName) => {
    return {
      params: {
        id: fileName.replace(/\.md$/, '')
      }
    }
  })
}

export async function getPostData(id: string) {
  const fullPath = path.join(postsDirectory, `${id}.md`);
  const fileContents = fs.readFileSync(fullPath, 'utf8');

  // use gray-matter to parse the post metadata section
  const matterResult = matter(fileContents);

  // use remark to convert markdown to HTML string
  const processedContent = await remark().use(html).process(matterResult.content)
  const contentHTML = processedContent.toString();

  // Combine data with the id and contentHTML

  return {
    id, 
    contentHTML,
    ...matterResult.data as {date: string, title: string}
  }
}


/**
 * uploadNewPost creates a new markdown file with the given info
 * @param title 
 * @param text 
 * @returns Promise<string>
 */
export function uploadNewPost(title: string, text: string) {
  return new Promise<string>((resolve, reject) => {
    // create url id / filename
    const url = title.toLowerCase().replace(' ', '-');
    // check to see if unique title
    const fileNames = fs.readdirSync(postsDirectory);
    fileNames.forEach((filename) => {
      // get ride of .md
      if (filename.replace(/\.md$/, '') == url) {
        reject('Title Already Exists');
        return;
      }
    });
    // create write stream
    const fileStream = fs.createWriteStream(('../posts/' + url + '.md'));
    // write to the md file
    fileStream.write('---\r\n');
    fileStream.write('title: \'' + title + '\'\r\n');
    fileStream.write('date: \'' + new Date() + '\'\r\n');
    fileStream.write('---\r\n\r\n');
    fileStream.write(text);
    // create finish event callback
    fileStream.on('finish', () => {
      resolve('Finished Creating Post');
    });
    // create errror event callback
    fileStream.on('error', (err) => {
      reject(err);
    })
  });
}
